
public class PruebaNumeros {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numero1, numero2, resultado;
		
		numero1=30;
		numero2=15;
		
		resultado=numero1+numero2;
		System.out.println(numero1+" + "+ numero2+" = "+resultado);
		
		resultado=numero1-numero2;
		System.out.println(numero1+" - "+ numero2+" = "+resultado);
		
		resultado=numero1*numero2;
		System.out.println(numero1+" * "+ numero2+" = "+resultado);
		
		resultado=numero1/numero2;
		System.out.println(numero1+" / "+ numero2+" = "+resultado);
	}

}
